int main(int c,char**v){char*l=NULL;size_t n=0;getline(&l,&n,fopen(v[1],"r"));long x=0,y=0,a,b,i,j,k;char*e,*s=strtok(l,","),m[128];do{for(i=strtol(s,&e,10);i<=strtol(e+1,NULL,10);i++){c=sprintf(m,"%ld",i);b=0;for(j=1;j<=c/2;j++){if(c%j)continue;a=0;for(k=1;k<c/j;k++)if(strncmp(m,m+k*j,j))a=1;if(!a){if(c%2==0&&j==c/2)x+=i;if(!b){y+=i;b=1;}}}}}while(s=strtok(NULL,","));printf("%ld\n%ld\n",x,y);}
